import React, { useState } from 'react';
import './Header.css';
import NewRow from './NewRow';

const Header = () => {



    const arr = [
        { alertName: 'alert1', frequency: 10, indep_option: ['M', "n"], dep_option: "M" },
        { alertName: 'alert1', frequency: 10, indep_option: ['M', "n"], dep_option: "M" },
        { alertName: '', frequency: 0, indep_option: [], dep_option: "" }
    ]
    const [ar, setAr] = useState(arr);

    return (
        <div className="Header">
            <header className="app-header">
                <h1>Alert Configuration</h1>
                <hr />
                <div className='headings'>
                    <div className='add' onClick={() => {ar.push({ alertName: '', frequency: 0, indep_option: [], dep_option: "" })}}>+</div>
                    <div>Alert Name</div>
                    <div>Independent Variable</div>
                    <div>Dependent Variable</div>
                    <div>Frequency</div>
                    <div>Save</div>
                </div>
                <hr />
            </header>

            {ar.map((ele) => (
                <NewRow alertName={ele.alertName} io={ele.indep_option} do={ele.dep_option} freq={ele.frequency} />
            ))}

        </div>
    );
}

export default Header;
