import React, { useState } from 'react';
import './Header.css';
import NewRow from './NewRow';

const Header = () => {

    const arr = [
        { alertName: 'alert1', frequency: 10, indep_option: ['M', "n"], dep_option: "M" },
        { alertName: 'alert1', frequency: 10, indep_option: ['M', "n"], dep_option: "M" },
        { alertName: '', frequency: 0, indep_option: [], dep_option: "" }
    ]
    const [ar, setAr] = useState(arr);

    const setNewRow = () => {

        let newRow = [...ar, { alertName: '', frequency: 0, indep_option: [], dep_option: "" }];
        // setAr(arr.push({ alertName: '', frequency: 0, indep_option: [], dep_option: "" })
        setAr(newRow);

    }

    const removeRow = (index) => {
        // let newRemovedArray = [...ar]
        // newRemovedArray[index].alertName = "";
        // newRemovedArray[index].dep_option = [''];
        // newRemovedArray[index].frequency = "";
        // newRemovedArray[index].indep_option = [''];
        // let arrayToSet = newRemovedArray.filter((item,i) => i !== index)
        // let array = [...ar];
        console.log(ar,"array",index);
        let newRemovedArray = ar.splice(index,1);
        console.log(ar,"newar");
        setAr(ar);
    }

    const addData = (term,freq,selectedOption,index, multiSelectOption) => {
        let arToUpdate = [...ar];
        arToUpdate[index].alertName = term;
        arToUpdate[index].dep_option = selectedOption;
        arToUpdate[index].frequency = freq;
        arToUpdate[index].indep_option = multiSelectOption;

        console.log("Codeeeeeeeeeeeeee",arToUpdate);
    }

    return (
        <div className="Header">
            <header className="app-header">
                <h1>Alert Configuration</h1>
                <hr />
                <div className='headings'>
                    <div className='add' onClick={() => setNewRow()}>+</div>
                    <div>Alert Name</div>
                    <div>Independent Variable</div>
                    <div>Dependent Variable</div>
                    <div>Frequency</div>
                    <div>Save</div>
                </div>
                <hr />
            </header>

            {ar.map((ele,idx) => (
                <NewRow addData={addData} removeRow={removeRow} key={idx} indexValue={idx} alertName={ele.alertName} io={ele.indep_option} do={ele.dep_option} freq={ele.frequency} />
            ))}

        </div>
    );
}

export default Header;
