import React from "react";
import Select from 'react-select';
import './NewRow.css';

class NewRow extends React.Component {

    state = {
        selectedOption: null,
        term:this.props.alertName,
        freq:this.props.freq
    };


    handleChange = (selectedOption) => {
        this.setState({ selectedOption });
        console.log(`Option selected:`, selectedOption);
    };


    render() {

        const { selectedOption,term,freq } = this.state;

        const op1 = [
            { label: "ME Torge (Avg.)", value: 355 },
            { label: "ME Speed (Avg.)", value: 54 },
            { label: "Rate of Return (Avg.)", value: 43 }
        ];

        const op2 = [
            { label: "ME Torge (Avg.)", value: 355 },
            { label: "ME Speed (Avg.)", value: 54 },
            { label: "Rate of Return (Avg.)", value: 43 }
        ];


        return (
            <div className="flex">
                <div className="remove">X</div>
                <input className="alertName" type="text" value={term} onChange={(e) => this.setState({term : e.target.value})}></input>
                <Select className="multiselect" options={op1}
                    isMulti />
                <Select className="singleselect" options={op2} onChange={this.handleChange} value={this.props.do} />
                <input className="number" value={freq} type="number" onChange={(e) => this.setState({freq : e.target.value})}></input>
                <button>Save</button>
            </div>
        );
    }

}

export default NewRow;
